<?phpuse yii\helpers\Html;use alkurn\thumbnail\ThumbnailImage;use yii\helpers\StringHelper;/** * Created by PhpStorm. * User: ganesh * Date: 6/7/17 * Time: 12:07 PM */?><?= Html::a(Html::encode($model->title), ['view', 'id' => $model->id]); ?><div class="blog-list">    <div class="blog-img"> <?php echo Html::a(ThumbnailImage::thumbnailImg($model->image, 100, 100), ['view', 'id' => $model->id]); ?> </div>    <div class="content">        <h2><?= Html::a(Html::encode($model->title), ['/blog', 'id' => $model->id]); ?></h2>        <?= StringHelper::truncate($model->content, 300); ?>    </div>    <?php echo Html::a('View More', ['/blog', 'id' => $model->id], ['class'=>"btn btn-primary"]); ?>    <div class="clearfix"></div></div>